  "examples": [
    {
      "id": "S0363",
      "name": "Empire",
      "description": "<a href=\"/software/S0363\">Empire</a> can use Inveigh to conduct name service poisoning for credential theft and associated relay attacks.",
      "references": [
        {"id": "scite-8", "text": "Schroeder, W., Warner, J., Nelson, M. (n.d.). Github PowerShellEmpire. Retrieved April 28, 2016.", "url": "https://github.com/PowerShellEmpire/Empire"},
        {"id": "scite-9", "text": "Robertson, K. (2015, April 2). Inveigh: Windows PowerShell ADIDNS/LLMNR/mDNS/NBNS spoofer/man-in-the-middle tool. Retrieved March 11, 2019.", "url": "https://github.com/Kevin-Robertson/Inveigh"}
      ]
    },
    {
      "id": "S0357",
      "name": "Impacket",
      "description": "<a href=\"/software/S0357\">Impacket</a> modules like ntlmrelayx and smbrelayx can be used in conjunction with <a href=\"/techniques/T1040\">Network Sniffing</a> and <a href=\"/techniques/T1557/001\">LLMNR/NBT-NS Poisoning and SMB Relay</a> to gather NetNTLM credentials for <a href=\"/techniques/T1110\">Brute Force</a> or relay attacks that can gain code execution.",
      "references": [
        {"id": "scite-10", "text": "SecureAuth. (n.d.).  Retrieved January 15, 2019.", "url": "https://www.secureauth.com/labs/open-source-tools/impacket"}
      ]
