  "mitigations": [
    {
      "id": "M1042",
      "name": "Disable or Remove Feature or Program",
      "description": "Disable LLMNR and NetBIOS in local computer security settings or by group policy if they are not needed within an environment.",
      "references": [
        {"id": "scite-15", "text": "Metcalf, S. (2016, October 21). Securing Windows Workstations: Developing a Secure Baseline. Retrieved November 17, 2017.", "url": "https://adsecurity.org/?p=3299"}
      ]
    },
    {
      "id": "M1037",
      "name": "Filter Network Traffic",
      "description": "Use host-based security software to block LLMNR/NetBIOS traffic. Enabling SMB Signing can stop NTLMv2 relay attacks.",
      "references": [
        {"id": "scite-3", "text": "Salvati, M. (2017, June 2). Practical guide to NTLM Relaying in 2017 (A.K.A getting a foothold in under 5 minutes). Retrieved February 7, 2019.", "url": "https://byt3bl33d3r.github.io/practical-guide-to-ntlm-relaying-in-2017-aka-getting-a-foothold-in-under-5-minutes.html"},
        {"id": "scite-4", "text": "Kuehn, E. (2018, April 11). Ever Run a Relay? Why SMB Relays Should Be On Your Mind. Retrieved February 7, 2019.", "url": "https://blog.secureideas.com/2018/04/ever-run-a-relay-why-smb-relays-should-be-on-your-mind.html"},
        {"id": "scite-16", "text": "Microsoft. (2008, September 10). Using SMB Packet Signing. Retrieved February 7, 2019.", "url": "https://docs.microsoft.com/en-us/previous-versions/system-center/operations-manager-2005/cc180803(v=technet.10)"}
      ]
    },
    {
      "id": "M1031",
      "name": "Network Intrusion Prevention",
      "description": "Network intrusion detection and prevention systems that can identify traffic patterns indicative of AiTM activity can be used to mitigate activity at the network level."
    },
    {
      "id": "M1030",
      "name": "Network Segmentation",
      "description": "Network segmentation can be used to isolate infrastructure components that do not require broad network access. This may mitigate, or at least alleviate, the scope of AiTM activity."
    }
  ],
  "detections": [
    {
      "id": "DS0029",
      "name": "Network Traffic",
      "components": [
        {"name": "Network Traffic Content", "description": "Monitor for traffic on ports UDP 5355 and UDP 137 if LLMNR/NetBIOS is disabled by security policy.", "url": "/datasources/DS0029/#Network%20Traffic%20Content"},
        {"name": "Network Traffic Flow", "description": "Monitor for network traffic originating from unknown/unexpected hardware devices. Local network traffic metadata (such as source MAC addressing) as well as usage of network management protocols such as DHCP may be helpful in identifying hardware.", "url": "/datasources/DS0029/#Network%20Traffic%20Flow"}
      ]
    },
    {
      "id": "DS0019",
      "name": "Service",
      "components": [
        {"name": "Service Creation", "description": "Monitor for newly constructed services/daemons through Windows event logs for event IDs 4697 and 7045. Deploy an LLMNR/NBT-NS spoofing detection tool.", "url": "/datasources/DS0019/#Service%20Creation"}
      ]
    },
    {
      "id": "DS0024",
      "name": "Windows Registry",
      "components": [
        {"name": "Windows Registry Key Modification", "description": "Monitor HKLM\\Software\\Policies\\Microsoft\\Windows NT\\DNSClient for changes to the \"EnableMulticast\" DWORD value. A value of \"0\" indicates LLMNR is disabled.", "url": "/datasources/DS0024/#Windows%20Registry%20Key%20Modification"}
      ]
    }
  ],
